package Loop;

public class PatternNumber {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int num=1;
		for(int i=1;i<=4;i++)
		{

			//System.out.print(" ");
		
			for(int j=1;j<=i;j++)
			{
				System.out.print(num+" ");
			num++;
			}
			//System.out.println(" ");
		
		System.out.println(" ");

	}

	}}
